import React, { useState, useEffect } from "react";
export default function useDebounce(value, delay) {
  const {
    0: debouncedValue,
    1: setDebouncedValue
  } = useState(value);
  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);
    return () => {
      clearTimeout(handler);
    };
  }, [value]);
  return debouncedValue;
}
