import { SHOP } from "../actionTypes";
import * as shopApis from "../../apis/shop"; //Get all products

export var fetchProducts = function fetchProducts() {
  return {
    type: SHOP.FETCH_PRODUCTS
  };
};
export var fetchProductsSuccess = function fetchProductsSuccess(data, productCount) {
  return {
    type: SHOP.FETCH_PRODUCTS_SUCCESS,
    payload: {
      data: data,
      productCount: productCount
    }
  };
};
export var fetchProductsFail = function fetchProductsFail(err) {
  return {
    type: SHOP.FETCH_PRODUCTS_FAIL,
    payload: {
      err: err
    }
  };
};
export var fetchProductsRequest = function fetchProductsRequest(query) {
  return function (dispatch) {
    dispatch(fetchProducts());
    shopApis.fetchProductsData(query).then(function (res) {
      dispatch(fetchProductsSuccess(res.data, res.headers["x-total-count"]));
    })["catch"](function (err) {
      dispatch(fetchProductsFail(err));
    });
  };
}; //Get sale products

